<?phpif (!defined('_PS_VERSION_')) {    exit;}class SmilePayMsg extends Module{    /** Construction of module  **/    public function __construct()    {        $this->name = 'smilepaymsg';        $this->tab = 'smilepaymsg';        $this->version = '2.1.10';        $this->author = 'Smilepay';        $this->bootstrap = true;        parent::__construct();        $this->displayName = $this->l('smilepaymsg');        $this->description = $this->l('SmilePay message table');    }    public function install()    {        if (!parent::install()            || !$this->registerHook('displayAdminOrderTabOrder')            || !$this->registerHook('displayAdminOrderContentOrder')        ) {            return false;        }        return true;    }    /** Module functions  **/    public function hookDisplayAdminOrderTabOrder($params)    {        $rq = Db::getInstance()->getRow('SELECT payment_message FROM `'            . _DB_PREFIX_ . 'orders` WHERE id_order=' . $params['order']->id);        $payment_message = $rq['payment_message'];        if (strlen($payment_message) > 0) {            return $this->display(__FILE__, '/views/templates/hook/tab_order.tpl');        }    }    public function hookDisplayAdminOrderContentOrder($params)    {        $order = $params['order'];        $rq = Db::getInstance()->getRow('SELECT payment_message FROM `' . _DB_PREFIX_ . 'orders` WHERE id_order=' . $order->id);        $button = array_pop($rq);        $this->smarty->assign(array(            'order' => $params['order'],            'buttonn' => $button,        ));        return $this->display(__FILE__, '/views/templates/hook/content_order.tpl');    }}